import 'package:flutter_riverpod/flutter_riverpod.dart';import '../../providers.dart';class Calculate {  ///1日合計  int sumAllDay(WidgetRef ref) {    var profitAllDaySum =        ref.watch(allDayProfitProvider).reduce((a, b) => a + b);    return profitAllDaySum;  }  ///1日平均(営業時間 ÷ 総利益)  int averageAllDay(WidgetRef ref) {    var profitAllDayAverage =        ref.watch(allDayProfitProvider).reduce((a, b) => a + b) /            ref.watch(allDayProfitProvider).length;    return profitAllDayAverage.toInt();  }}