import 'package:flutter_riverpod/flutter_riverpod.dart';import '../providers.dart';// 設定した通知リスト取得String getNotices(WidgetRef ref) {  List noticeList = ref.watch(noticeListProvider);  String retString = "";  //インデックス番号  int i = 0;  // セットした値取り出し  for (String notice in noticeList) {    String ele = "・" + notice;    retString = "$ele$retString　";  }  return retString;}//管理者権限setNotices(WidgetRef ref, List<String> noticeList) {  //インデックス番号  int i = 0;  // セットした値取り出し  for (String notice in noticeList) {    int Index = i + 1;    String ele = Index.toString() + notice; // Dartはダート  }  // String ntcValue = noticeList.toString();  // for (ntcValue.substring("sdf")) {  //  // }  // リストに追加  ref.read(noticeListProvider.notifier).state = noticeList;}