riteUserList.length,      // Image.asset('assets/imgs/appLogo.png'),      (index) => Image.network(        ImageUrls.favoriteUserList[index],        fit: BoxFit.cover,        errorBuilder: (c, o, s) {          return const Icon(            Icons.error,            color: Colors.red,            size: 40,          );        },      ),    );