import 'package:flutter/material.dart';import 'package:flutter_riverpod/flutter_riverpod.dart';///共通Footer(勤怠、コスト計算、カメラview)class BottomNaviBar extends ConsumerWidget {  const BottomNaviBar({Key? key}) : super(key: key);  @override  Widget build(BuildContext context, WidgetRef ref) {    return Expanded(        child: Row(      children: [        //勤怠ボタン        ElevatedButton.icon(          icon: const Icon(            //時間Icons            Icons.calendar_today,            color: Colors.white,          ),          label: const Text('timeSchedule'),          style: ElevatedButton.styleFrom(            primary: Colors.green,            onPrimary: Colors.white,          ),          onPressed: () {},        ),        //コスト参照ボタン(Top画面)        ElevatedButton.icon(          icon: const Icon(            Icons.dashboard,            color: Colors.white,          ),          label: const Text('top'),          style: ElevatedButton.styleFrom(            primary: Colors.green,            onPrimary: Colors.white,          ),          onPressed: () {},        ),        //履歴画面        ElevatedButton.icon(          icon: const Icon(            Icons.history,            color: Colors.white,          ),          label: const Text('history'),          style: ElevatedButton.styleFrom(            primary: Colors.green,            onPrimary: Colors.white,          ),          onPressed: () {},        ),      ],    ));  }}