import 'package:flutter/material.dart';import 'package:flutter_riverpod/flutter_riverpod.dart';import '../../screen/common_widget/settings/setting_top.dart';import '../../screen/login/login_top.dart';/// 共通Drawerclass CommonDrawer extends ConsumerWidget {  const CommonDrawer({Key? key}) : super(key: key);  @override  Widget build(BuildContext context, WidgetRef ref) {    return SizedBox(      width: 250,      child: Drawer(        child: ListView(          children: <Widget>[            SizedBox(              height: 160.0,              child: DrawerHeader(                decoration: const BoxDecoration(                  color: Colors.indigo,                  // image:    //                ),                child: Center(                  //  todo マイメニューアイコン                  child: GestureDetector(                      child: Image.asset('assets/imgs/user_icon.png')),                  //     Text(                  //   '2 notices',                  //   style: TextStyle(fontSize: 30, color: Colors.amber),                  // )                ),              ),            ),            //---------リスト実態            const ListTile(              title: Text("tutorial"),              subtitle: Text("このアプリについて"),              leading: Padding(                padding: EdgeInsets.all(8.0),                child: Icon(                  Icons.account_balance_outlined,                ),              ),            ),            const ListTile(              title: Text("Message"),              subtitle: Text("メッセージ"),              leading: Padding(                padding: EdgeInsets.all(8.0),                child: Icon(                  Icons.mail_outline,                ),              ),            ),            // const ListTile(            //   title: Text("shift schedule"),            //   subtitle: Text("シフト作成"),            //   leading: Padding(            //     padding: EdgeInsets.all(8.0),            //     child: Icon(            //       Icons.edit_calendar_rounded,            //     ),            //   ),            //   trailing: Icon(Icons.warning_amber_rounded, color: Colors.amber),            // ),            const Divider(              height: 0,              endIndent: 0,              color: Colors.white70,            ),            ListTile(              title: const Text("settings"),              subtitle: const Text("設定"),              leading: const Padding(                padding: EdgeInsets.all(8.0),                child: Icon(                  Icons.settings,                ),              ),              onTap: () {                Navigator.push(                  context,                  MaterialPageRoute(builder: (context) => const SettingTop()),                );              },            ),            //ログアウト            ListTile(              title: const Text("log out"),              subtitle: const Text("ログアウト"),              leading: const Padding(                padding: EdgeInsets.all(8.0),                child: Icon(                  Icons.transit_enterexit_outlined,                ),              ),              onTap: () {                //User情報初期化                ref.read(userNameProvider.notifier).state = "";                //todo 情報初期化 API                //ログイン画面へ遷移                Navigator.push(                  context,                  MaterialPageRoute(builder: (context) => const LoginTop()),                );              },            ),            const Divider(              height: 0,              endIndent: 0,              color: Colors.white70,            ),            ListTile(              title: const Text("Withdrawal"),              subtitle: const Text("退会"),              leading: const Padding(                padding: EdgeInsets.all(8.0),                child: Icon(                  Icons.outlet,                ),              ),              onTap: () {                Navigator.push(                  context,                  MaterialPageRoute(builder: (context) => const SettingTop()),                );              },            ),          ],        ),      ),    );  }}