import 'package:flutter/material.dart';import 'package:flutter_riverpod/flutter_riverpod.dart';import 'package:marquee/marquee.dart';import '../../view_functions.dart';///共通ヘッダ(動的Header)class CommonDynamicAppbar extends ConsumerWidget {  const CommonDynamicAppbar(this.amount, {Key? key}) : super(key: key);  //1日合計値  final String amount;  @override  Widget build(BuildContext context, WidgetRef ref) {    return AppBar(      //　AppBar　戻るBtn非活性処理(Defaultあり)      automaticallyImplyLeading: false,      title: Center(        child: SizedBox(          //todo メディアクエリ自走          height: MediaQuery.of(context).size.height,          child: Marquee(              //通知              text: getNotices(ref),              blankSpace: 40, //空白              velocity: 80, //速さ              startPadding: 8),        ),      ),    );  }  Future<void> exeFunc(context, ref) async {    //  電話かける処理  }  Future<void> cxlFunc(context, ref) async {    //  closeのみ    Navigator.of(context).pop();  }}///共通ヘッダ(静的Header)class CommonStaticAppbar extends ConsumerWidget {  const CommonStaticAppbar(this.ttl, {Key? key}) : super(key: key);  final String ttl;  @override  Widget build(BuildContext context, WidgetRef ref) {    return AppBar(      // タイトルテキスト      title: Center(        child: Text(          ttl,          style: const TextStyle(            fontSize: 18,          ),        ),      ),      // 右側アイコン      actions: const <Widget>[],    );  }}