import 'package:flutter/material.dart';import 'package:flutter_riverpod/flutter_riverpod.dart';import '../../../../../../providers.dart';class HourProfit extends ConsumerWidget {  const HourProfit({Key? key}) : super(key: key);  @override  Widget build(BuildContext context, WidgetRef ref) {    return Padding(      padding: const EdgeInsets.only(left: 130),      child: Column(        children: [          const Text("profit/h"),          Text(            calculate(ref),            style: const TextStyle(              fontSize: 20,              fontWeight: FontWeight.bold,              color: Colors.greenAccent,            ),          ),        ],      ),    );  }  String calculate(WidgetRef ref) {    //格納要素合計    var profitSum = ref.read(oneHourProfitProvider).reduce((a, b) => a + b);    return profitSum.toString();  }}