import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:flutter_riverpod/flutter_riverpod.dart';import '../../../../../../providers.dart';/// 時間コスト表示txtclass HourCostTxt extends ConsumerWidget {  const HourCostTxt({Key? key}) : super(key: key);  @override  Widget build(BuildContext context, WidgetRef ref) {    return Padding(      padding: const EdgeInsets.only(left: 50),      child: Column(        children: [          const Text("laborCost/h"),          Text(            ref.read(oneHourCostProvider).toString(),            style: const TextStyle(              fontSize: 20,              fontWeight: FontWeight.bold,              // color: Colors.redAccent,            ),          ),        ],      ),    );  }}